import PortfolioLanding from '../PortfolioLanding';
import style from './styles.scss';

export default class PortfolioLayout extends React.Component {
  render() {
    const { title, brief, work } = this.props;
    return (
      <div>
        <PortfolioLanding title={title} />
        <div className={style.brief}>{brief}</div>
        <div className={style.tasks}>
          <ul>
            {work.map((task, key) => (
              <li className={style.task} key={task}>
                {task}
              </li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}
