import React from 'react';
import Link from 'next/link';
import Button from '../Button/';

export default class PortfolioLink extends React.Component {
  render() {
    const { id, title, summary, background } = this.props;
    return (
      <Link
        as={`/portfolio/${id}`}
        href={`/post?title=${title}`}
        summary={summary}>
        <a>
          <Button classes={background} text='View Project' />
        </a>
      </Link>
    );
  }
}
