import PortfolioLanding from '../PortfolioLanding';
import style from './styles.scss';
import PortfolioBrief from '../PortfolioBrief';

export default class PortfolioLayout extends React.Component {
  render() {
    const { title, background, url, brief, work, technology } = this.props;
    return (
      <div>
        <PortfolioLanding title={title} background={background} url={url} />
        <PortfolioBrief brief={brief} />
        <h2>Key Technologies</h2>
        <div className={style.tasks}>
          <ul>
            {technology.map((tech, key) => (
              <li className={style.task} key={tech.id}>
                {tech.name}
              </li>
            ))}
          </ul>
        </div>
        <h2>How I Helped</h2>
        <div className={style.tasks}>
          <ul>
            {work.map((task, key) => (
              <li className={style.task} key={task}>
                {task}
              </li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}
